import React, { useState } from 'react'
import "./TodoAdv.css"

function TodoAdv() {
    let [text,setText]= useState('')
   let [todo,setTodo]=useState([])
   let [edit,setEdit]=useState('')
   function textHandler(e)
   {
    setText(e.target.value)
   }
   function submitHandler()
   {
     if(text.length===0)
     {
        alert('not possible')
     }
     else{
        let obj={
            id:new Date().getMilliseconds(),
            msg:text
          }
          if(edit)
          {
            //edit
           setTodo((pre)=>{
            return pre.map((data)=>{
                 if(data.id===edit.id)
                 {
                     data.msg=text;
                 }
                 return data
            })
           })
           setEdit('')
          }
          //add
          else{
            setTodo((pre)=>{
                return [...pre,obj]
               })
          }
           
           setText('')
     }



    
   }
//deletion

function deleteHandler(id)
{
    console.log(id)
    setTodo((pre)=>{
        return pre.filter((data)=>{
            return data.id !==id
        })

    })
}
//edit logic
function editHandler(id)
{
   let x=   todo.find((data)=>{
        return data.id===id
     })
     setEdit(x)
     setText(x.msg)
}
  return (
    <>
      <div className="container-fluid">
        <div className="row bg-secondary p-2 m-2">
            <input type="text" placeholder='Enter your text' onChange={textHandler} value={text} />
            <button className='bg-success'>add</button>
        </div>
        <div className="row d-flex">
            <div className="col flex-column step1">
                <h1 align="center">STEP-1</h1>
                { todo.length===0?<h1>no data</h1>:todo.map((data)=>{
            return <div className="col">
                <div className="card">
                <div className="body">
                      {data.msg}
                    <button className='btn btn-primary' onClick={()=>{
                        deleteHandler(data.id)
                    }}>del</button>
                    <button className='btn btn-info'
                    onClick={()=>{
                        editHandler(data.id)
                    }}>edit</button>
                </div>
            </div>
        </div> 
           })}  
            </div>
            <div className="col flex-column step2">
                <h1 align="center">STEP-2</h1>
            </div>
            <div className="col flex-column  step3">
                <h1 align="center">STEP-3</h1>
            </div>
        </div>
      </div>
    </>
  )
}

export default TodoAdv
