import React,{useContext} from "react";
import "../Style/Product.css"
import { ContextProduct } from "../Context/ContextData";


function Product() {
  let Items=useContext(ContextProduct)
  It
  return (
    <>
         <div className="row">
                    <div className="col search">
                         Search:<input type='search'  placeholder='search your name here'></input>
                         <select className='p-2 m-2'>
                        <option value="" >..Filter..</option>
                        <option value="all" >A-Z</option>
                        <option value="revall" >Z-A</option>
                        <option value="ascending" >1-10</option>   
                        <option value="descending" >10-1</option>
                    </select>
                    {/* <button className='btn btn-Primery'>Submit</button> */}
                    </div>
                </div>   

      <div className="container-fluid bg-success" style={{ height: "auto" ,width:"100vw"}}>
        <h1>Select Yopur Choice Of Tools</h1>
        <div className="row d-flex justify-content-center bg-info">
          {Items.map((data, i) => {
            return (
              <div
                className="card shadow p-4 m-3  rounded bg-secondary"
                style={{ width: "18rem" }}
                key={i}
              >
                <img
                  className="card-url-top rounded "
                  src={data.url}
                  alt="pic"
                />
                <div className="card-body bg-warning">
                  <h5 className="card-title">{data.name}</h5>
                  <ul className="card-text" type="circle">
                    <li>BRAND :{data.brand}</li>
                    <li>MATERIAL :{data.material}</li>
                    <li>COLOR :{data.color}</li>
                    <li>cATAGORY :{data.catagory}</li>
                  </ul>
                  <h1 className="bg-danger">â‚¹: {data.price}</h1>
                  <a href={data.buy} className="btn btn-primary">
                    Buy Now
                  </a>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </>
  );
}

export default Product;
