import React,{useContext} from "react";
import "../Style/FeturesData.css"
import {ContextProduct } from "../Context/ContextData";

function FeturesData() {
let data=useContext(ContextProduct)
  data=data.filter((ele)=>{
    if (ele.feature===true)
    {
      return ele;
    }
  })
  return (
    <>
      {data.map((card) => {
        return (
          <>
            <div className="FCards" key={card.id}>
              <img className="image" src={card.img} alt={card.name} />
              
              <h5 className="Name">{card.name}</h5>
              <p className="Desc">{card.desc}</p>
            </div>
          </>
        );
      })}
    </>
  );
}

export default FeturesData;
