import React,{useContext} from "react";
import "../Style/FeturesData.css"
import {ContextProduct } from "../Context/ContextData";

function FeturesData() {
let data=useContext(ContextProduct)
  data=data.filter((ele)=>{
    if (ele.feature===true)
    {
      return ele;
    }
  })
  return (
    <>
      {data.map((card) => {
        return (
          <>
            <div className="container-fluid" style={{ height:"auto",width:"100vw"}}>
        <h1>Select Yopur Choice Of Tools</h1>
        <div className="row d-flex justify-content-center">
          {Items.map((data, i) => {
            return (
              <div
                className="card shadow p-4 m-3  rounded "
                style={{ width: "17rem" }}
                key={i}
              >
                <img
                  className="card-url-top rounded "
                  src={data.url}
                  alt="pic"
                />
                <div className="card-body">
                  <h3 className="card-title">{data.name}</h3>
                  <h1>â‚¹: {data.price}</h1>
                  <ul className="card-text" type="circle">
                    <li>BRAND :{data.brand}</li>
                    <li>MATERIAL :{data.material}</li>
                    <li>COLOR :{data.color}</li>
                    <li>CATAGORY :{data.catagory}</li>
                  </ul>
                  
                  <a href={data.buy} className="btn btn-primary">
                    Buy Now
                  </a>
                </div>
              </div>
            );
          })}
        </div>
      </div>
          </>
        );
      })}
    </>
  );
}

export default FeturesData;
